}

#oooh {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw!important;
  height: 100vh!important;
  touch-action: none; /* para gestos táctiles */
  z-index: -1; /* opcional, si querés que esté detrás de otros elementos */
}

manifest.Script:
const app = document.querySelector('#apps-box #Freshpaint');
const colors = ['#ff4b4b', '#4bdcff', '#a2ff4b'];
let index = 0;

const canvas = document.querySelector("#oooh");
const ctx = canvas.getContext("2d");
ctx.fillStyle = "black";
canvas.addEventListener("touchmove", (e) => {
  e.preventDefault();
  for (let touch of e.touches) {
    const x = touch.clientX;
    const y = touch.clientY;
    ctx.beginPath();
    ctx.arc(x, y, 12, 0, Math.PI * 2);
    ctx.fill();
  }
});
