manifest.Stylesheet:
      .clock {
        font-size: 48px;
        color: #fff;
        text-align: center;
        padding: 40px;
        background: #222;
      }

      .theme-toggle {
        font-size: 16px;
        padding: 12px;
        background: #0078d7;
        color: #fff;
        cursor: pointer;
        margin-top: 20px;
        text-align: center;
      }

manifest.Script:
      const app = document.querySelector('#apps-box #metroclock');
      const clock = app.querySelector('#clock');
      const toggle = app.querySelector('#theme-toggle');

      function updateTime() {
        const now = new Date();
        clock.textContent = now.toLocaleTimeString();
      }

      setInterval(updateTime, 1000);
      updateTime();

      toggle.onclick = () => {
        const dark = app.hasAttribute('data-dark');
        if (dark) {
          app.removeAttribute('data-dark');
          clock.style.background = '#222';
        } else {
          app.setAttribute('data-dark', 'true');
          clock.style.background = '#000';
        }
      };
